import React from 'react';
import '../styles/Testimonials.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination, Autoplay, Navigation } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import 'swiper/css/navigation';
import { FaStar, FaCheckCircle } from 'react-icons/fa';
import { motion } from 'framer-motion';
import { testimonials } from './Data';

function Testimonials() {
  return (
    <section className="testimonials">
      <div className="row align-items-center">
        <div className="col-lg-5 info">
          <h3>Testimonials</h3>
          <p>
            Hear directly from the people whose lives have been impacted by <b>Smart-Life Care</b>.
            Our commitment to fast, reliable, and compassionate ambulance services makes a real
            difference every day.
          </p>
        </div>

        <div className="col-lg-7">
          <Swiper
            modules={[Pagination, Autoplay, Navigation]}
            loop={true}
            speed={800}
            autoplay={{ delay: 3000, disableOnInteraction: false }}
            slidesPerView="auto"
            pagination={{ clickable: true }}
            navigation
          >
            {testimonials.map((testimonial) => (
              <SwiperSlide key={testimonial.name}>
                <motion.div
                  className="testimonial-item"
                  initial={{ opacity: 0, y: 50 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5 }}
                  viewport={{ once: true }}
                >
                  <div className="d-flex align-items-center">
                    <img
                      src={testimonial.src}
                      alt={testimonial.name}
                      className="testimonial-img"
                      loading="lazy"
                    />
                    <div className="testimonial-content">
                      <h3>
                        {testimonial.name}{' '}
                        <FaCheckCircle className="verified-badge" title="Verified" />
                      </h3>
                      <h4>{testimonial.job}</h4>
                      <div className="stars">
                        {[...Array(5)].map((_, index) => (
                          <FaStar key={index} className="star-icon" />
                        ))}
                      </div>
                    </div>
                  </div>
                  <p className="testimonial-comment">
                    <i className="quote-icon">&#8220;</i>
                    {testimonial.comment}
                    <i className="quote-icon">&#8221;</i>
                  </p>
                </motion.div>
              </SwiperSlide>
            ))}
          </Swiper>
        </div>
      </div>
    </section>
  );
}

export default Testimonials;
